example.code:
$( "div.left, div.right" ).find( "div, div > p" ).addClass( "border" );

// First Example
$( "div.before-addback" ).find( "p" ).addClass( "background" );

// Second Example
$( "div.after-addback" ).find( "p" ).addBack().addClass( "background" );

example.css:
p, div {
  margin: 5px;
  padding: 5px;
}
.border {
  border: 2px solid red;
}
.background {
  background: yellow;
}
.left, .right {
  width: 45%;
  float: left;
}
.right {
  margin-left: 3%;
}
