example.css:
p {
  margin: 8px;
  font-size: 16px;
}
.selected {
  color: blue;
}
.highlight {
  background: yellow;
}

p {
  margin: 8px;
  font-size: 16px;
}
.selected {
  color: red;
}
.highlight {
  background: yellow;
}

example.code:
$( "div" ).addClass(function( index, currentClass ) {
  var addedClass;

  if ( currentClass === "red" ) {
    addedClass = "green";
    $( "p" ).text( "There is one green div" );
  }

  return addedClass;
});

example.css:
div {
  background: white;
}
.red {
  background: red;
}
.red.green {
  background: green;
}
