example.code:
$( "select" )
  .change(function () {
    var str = "";
    $( "select option:selected" ).each(function() {
      str += $( this ).text() + " ";
    });
    $( "div" ).text( str );
  })
  .change();

$( "input[type='text']" ).change(function() {
  // Check input( $( this ).val() ) for validity here
});
