example.code:
var input = $( "form input:checkbox" )
  .wrap( "<span></span>" )
  .parent()
  .css({
    background: "yellow",
    border: "3px red solid"
  });

$( "div" )
  .text( "For this type jQuery found " + input.length + "." )
  .css( "color", "red" );

// Prevent the form from submitting
$( "form" ).submit(function( event ) {
    event.preventDefault();
});
