pre.code:
// jQuery 1.4.3+
$( elements ).delegate( selector, events, data, handler );
// jQuery 1.7+
$( elements ).on( events, selector, data, handler );

example.css:
p {
  background: yellow;
  font-weight: bold;
  cursor: pointer;
  padding: 5px;
}
p.over {
  background: #ccc;
}
span {
  color: red;
}

example.code:
$( "body" ).delegate( "p", "myCustomEvent", function( e, myName, myValue ) {
  $( this ).text( "Hi there!" );
  $( "span" )
    .stop()
    .css( "opacity", 1 )
    .text( "myName = " + myName )
    .fadeIn( 30 )
    .fadeOut( 1000 );
});
$( "button" ).click(function() {
  $( "p" ).trigger( "myCustomEvent" );
});

example.css:
p {
  color: red;
}
span {
  color: blue;
}
