example.code:
$( "p" ).click(function() {
  $( this ).toggleClass( "off" );
});
var p;
$( "button" ).click(function() {
  if ( p ) {
    p.appendTo( "body" );
    p = null;
  } else {
    p = $( "p" ).detach();
  }
});

example.css:
p {
  background: yellow;
  margin: 6px 0;
}
p.off {
  background: black;
}
