example.code:
jQuery.fn.showTags = function( n ) {
  var tags = this.map(function() {
    return this.tagName;
  })
  .get()
  .join(", ");
  $( "b:eq( " + n + " )" ).text( tags );
  return this;
};

$( "p" )
  .showTags( 0 )
  .find( "span" )
    .showTags( 1 )
    .css( "background", "yellow" )
  .end()
  .showTags( 2 )
  .css( "font-style", "italic" );

example.css:
p, div {
  margin: 1px;
  padding: 1px;
  font-weight: bold;
  font-size: 16px;
}
div {
  color: blue;
}
b {
  color: red;
}
