example.css:
div {
  height: 100px;
  width: 300px;
  margin: 10px;
  background-color: #ffd;
  overflow: auto;
}

example.code:
var last, diff;
$( "div" ).click(function( event ) {
  if ( last ) {
    diff = event.timeStamp - last;
    $( "div" ).append( "time since last event: " + diff + "<br>" );
  } else {
    $( "div" ).append( "Click again.<br>" );
  }
  last = event.timeStamp;
});
