example.code:
$( "span" ).click(function() {
  $( this ).fadeOut( 1000, function() {
    $( "div" ).text( "'" + $( this ).text() + "' has faded!" );
    $( this ).remove();
  });
});
$( "span" ).hover(function() {
  $( this ).addClass( "hilite" );
}, function() {
  $( this ).removeClass( "hilite" );
});

example.css:
span {
  cursor: pointer;
}
span.hilite {
  background: yellow;
}
div {
  display: inline;
  color: red;
}

.box,
button {
  float: left;
  margin: 5px 10px 5px 0;
}
.box {
  height: 80px;
  width: 80px;
  background: #090;
}
#log {
  clear: left;
}
