example.css:
p {
  width: 80px;
  margin: 0;
  padding: 5px;
}
div {
  width: 40px;
  height: 40px;
  position: absolute;
}
div#one {
  top: 0;
  left: 0;
  background: #f00;
}
div#two {
  top: 20px;
  left: 20px;
  background: #0f0;
}
div#three {
  top: 40px;
  left:40px;
  background:#00f;
}

example.code:
var getPos = function( n ) {
  return ( Math.floor( n ) * 90) + "px";
};
$( "p" ).each(function( n ) {
  var r = Math.floor( Math.random() * 3 );
  var tmp = $( this ).text();
  $( this ).text( $( "p:eq(" + r + ")" ).text() );
  $( "p:eq(" + r + ")" ).text( tmp );
  $( this ).css( "left", getPos( n ) );
});
$( "div" ).each(function( n ) {
  $( this ).css( "left", getPos( n ) );
})
  .css( "cursor", "pointer" )
  .click( function() {
    $( this ).fadeTo( 250, 0.25, function() {
      $( this )
        .css( "cursor", "" )
        .prev()
        .css({
          "font-weight": "bolder",
          "font-style": "italic"
          });
        });
    });

