example.code:
var newText = $( "p" ).text().split( " " ).join( "</span> <span>" );
newText = "<span>" + newText + "</span>";

$( "p" ).html( newText )
  .find( "span" )
    .hover(function() {
      $( this ).addClass( "hilite" );
    }, function() {
      $( this ).removeClass( "hilite" );
    })
  .end()
  .find( ":contains('t')" )
    .css({
      "font-style":"italic",
      "font-weight":"bolder"
    });

example.css:
p {
  font-size: 20px;
  width: 200px;
  cursor: default;
  color: blue;
  font-weight: bold;
  margin: 0 10px;
}
.hilite {
  background: yellow;
}
