example.code:
$( "*", document.body ).click(function( e ) {
  e.stopPropagation();
  var domEl = $( this ).get( 0 );
  $( "span:first" ).text( "Clicked on - " + domEl.nodeName );
});

example.css:
span {
  color: red;
}
div {
  background: yellow;
}

example.code:
function disp(divs) {
  var a = [];
  for ( var i = 0; i < divs.length; i++ ) {
    a.push( divs[ i ].innerHTML );
  }
  $( "span" ).text( a.join(" ") );
}
disp( $( "div" ).get().reverse() );
