example.code:
function showHeight( ele, h ) {
  $( "div" ).text( "The height for the " + ele + " is " + h + "px." );
}
$( "#getp" ).click(function() {
  showHeight( "paragraph", $( "p" ).height() );
});
$( "#getd" ).click(function() {
  showHeight( "document", $( document ).height() );
});
$( "#getw" ).click(function() {
  showHeight( "window", $( window ).height() );
});

example.css:
body {
