example.code:
$( "li" ).hover(
  function() {
    $( this ).append( $( "<span> ***</span>" ) );
  }, function() {
    $( this ).find( "span:last" ).remove();
  }
);

//li with fade class
$( "li.fade" ).hover(function() {
  $( this ).fadeOut( 100 );
  $( this ).fadeIn( 500 );
});

example.css:
ul {
  margin-left: 20px;
  color: blue;
}
li {
  cursor: default;
}
span {
  color: red;
}

ul {
  margin-left: 20px;
  color: blue;
}
li {
  cursor: default;
}
li.active {
  background: black;
  color: white;
}
span {
  color:red;
}
