pre.code:
$( "li" ).click(function() {
  var $li = $( this ),
    isWithTwo = $li.is(function() {
      return $( "strong", this ).length === 2;
    });
  if ( isWithTwo ) {
    $li.css( "background-color", "green" );
  } else {
    $li.css( "background-color", "red" );
  }
});

example.code:
$( "div" ).one( "click", function() {
  if ( $( this ).is( ":first-child" ) ) {
    $( "p" ).text( "It's the first div." );
  } else if ( $( this ).is( ".blue,.red" ) ) {
    $( "p" ).text( "It's a blue or red div." );
  } else if ( $( this ).is( ":contains('Peter')" ) ) {
    $( "p" ).text( "It's Peter!" );
  } else {
    $( "p" ).html( "It's nothing <em>special</em>." );
  }
  $( "p" ).hide().slideDown( "slow" );
  $( this ).css({
    "border-style": "inset",
    cursor: "default"
  });
});

example.css:
div {
  width: 60px;
  height: 60px;
  margin: 5px;
  float: left;
  border: 4px outset;
  background: green;
  text-align: center;
  font-weight: bolder;
  cursor: pointer;
}
.blue {
  background: blue;
}
.red {
  background: red;
}
span {
  color: white;
  font-size: 16px;
}
p {
  color: red;
  font-weight: bolder;
  background: yellow;
  margin: 3px;
  clear: left;
  display: none;
}

example.code:
var $alt = $( "#browsers li:nth-child(2n)" ).css( "background", "#0ff" );
$( "li" ).click(function() {
  if ( $alt.is( this ) ) {
    $( this ).slideUp();
  } else {
    $( this ).css( "background", "red" );
  }
});
