example.code:
var div = $( "div" )[ 0 ];
jQuery.data( div, "test", {
  first: 16,
  last: "pizza!"
});
$( "span:first" ).text( jQuery.data( div, "test" ).first );
$( "span:last" ).text( jQuery.data( div, "test" ).last );

example.css:
div {
  color: blue;
}
span {
  color: red;
}

example.code:
$( "button" ).click( function( e ) {
  var value, div = $( "div" )[ 0 ];
  switch ( $( "button" ).index( this ) ) {
    case 0 :
      value = jQuery.data( div, "blah" );
      break;
    case 1 :
      jQuery.data( div, "blah", "hello" );
      value = "Stored!";
      break;
    case 2 :
      jQuery.data( div, "blah", 86 );
      value = "Stored!";
      break;
    case 3 :
      jQuery.removeData( div, "blah" );
      value = "Removed!";
      break;
  }
  $( "span" ).text( "" + value );
});

example.css:
div {
  margin: 5px;
  background: yellow;
}
button {
  margin: 5px;
  font-size: 14px;
}
p {
  margin: 5px;
  color: blue;
}
span {
  color: red;
}
