example.code:
var arr = [ 1, 9, 3, 8, 6, 1, 5, 9, 4, 7, 3, 8, 6, 9, 1 ];
$( "div" ).text( arr.join(", ") );

arr = jQuery.grep(arr, function( n, i ) {
  return ( n != 5 && i > 4 );
});
$( "p" ).text( arr.join(", ") );

arr = jQuery.grep(arr, function( a ) {
  return a != 9;
});

$( "span" ).text( arr.join( ", " ) );

example.css:
div {
  color: blue;
}
p {
  color: green;
  margin: 0;
}
span {
  color: red;
}

example.code:
$.grep( [0, 1, 2 ], function( n, i ) {
    return n > 0;
}, true );
