example.code:
function stub() {}
var objs = [
  function() {},
  { x:15, y:20 },
  null,
  stub,
  "function"
];

jQuery.each( objs, function( i ) {
  var isFunc = jQuery.isFunction( objs[ i ]);
  $( "span" ).eq( i ).text( isFunc );
});

example.css:
div {
  color: blue;
  margin: 2px;
  font-size: 14px;
}
span {
  color: red;
}
