example.code:
$( "#show" ).click(function() {
  var n = jQuery.queue( $( "div" )[ 0 ], "fx" );
  $( "span" ).text( "Queue length is: " + n.length );
});

function runIt() {
  $( "div" ).show( "slow" );
  $( "div" ).animate({ left: "+=200" }, 2000 );
  $( "div" ).slideToggle( 1000 );
  $( "div" ).slideToggle( "fast" );
  $( "div" ).animate({ left: "-=200" }, 1500 );
  $( "div" ).hide( "slow" );
  $( "div" ).show( 1200 );
  $( "div" ).slideUp( "normal", runIt );
}

runIt();

example.css:
div {
  margin: 3px;
  width: 40px;
  height: 40px;
  position: absolute;
  left: 0px;
  top: 30px;
  background: green;
  display: none;
}
div.newcolor {
  background: blue;
}
span {
  color: red;
}

example.code:
$( document.body ).click(function() {
  $( "div" ).show( "slow" );
  $( "div" ).animate({ left: "+=200" }, 2000 );
  jQuery.queue( $( "div" )[ 0 ], "fx", function() {
    $(this).addClass( "newcolor" );
    jQuery.dequeue( this );
  });
  $( "div" ).animate({ left: "-=200" }, 500 );
  jQuery.queue( $( "div" )[ 0 ], "fx", function() {
    $( this ).removeClass( "newcolor" );
    jQuery.dequeue( this );
  });
  $( "div" ).slideUp();
});

