example.code:
$( document.body ).click(function() {
  $( document.body ).append( $( "<div>" ) );
  var n = $( "div" ).length;
  $( "span" ).text( "There are " + n + " divs." +
    "Click to add more.");
})
  .trigger( "click" ); // Trigger the click to start

example.css:
body {
  cursor: pointer;
}
div {
  width: 50px;
  height: 30px;
  margin: 5px;
  float: left;
  background: green;
}
span {
  color: red;
}
