example.code:
var p = $( "p:last" );
var offset = p.offset();
p.html( "left: " + offset.left + ", top: " + offset.top );

$( "*", document.body ).click(function( e ) {
  var offset = $( this ).offset();
  e.stopPropagation();
  $( "#result" ).text( this.tagName + " coords ( " + offset.left + ", " +
                                  offset.top + " )" );
});

example.css:
p {
  margin-left: 10px;
  color: blue;
  width: 200px;
  cursor: pointer;
}
span {
  color: red;
  cursor: pointer;
}
div.abs {
  width: 50px;
  height: 50px;
  position: absolute;
  left: 220px;
  top: 35px;
  background-color: green;
  cursor: pointer;
}
