example.css:
b, span, p, html body {
  padding: .5em;
  border: 1px solid;
}
b {
  color: blue;
}
strong {
  color: red;
}

example.code:
function showParents() {
  $( "div" ).css( "border-color", "white" );
  var len = $( "span.selected" )
    .parents( "div" )
    .css( "border", "2px red solid" )
    .length;
  $( "b" ).text( "Unique div parents: " + len );
}
$( "span" ).click(function () {
  $( this ).toggleClass( "selected" );
  showParents();
});

example.css:
p, div, span {
  margin: 2px;
  padding: 1px;
}
div {
  border: 2px white solid;
}
span {
  cursor: pointer;
  font-size: 12px;
}
.selected {
  color: blue;
}
