}

example.code:
var effect = function() {
  return $("div").fadeIn(800).delay(1200).fadeOut();
};

$("button").on( "click", function() {
  $("p").append( " Started... ");

  $.when( effect() ).done(function() {
    $("p").append(" Finished! ");
  });
});
