example.code:
$( "#start" ).click(function() {
  $( "div" ).show( "slow ");
  $( "div" ).animate({ left: "+=200" }, 5000 );
  $( "div" ).queue(function() {
    $( this ).addClass( "newcolor" );
    $( this ).dequeue();
  });
  $( "div" ).animate({ left: '-=200' }, 1500 );
  $( "div" ).queue(function() {
    $( this ).removeClass( "newcolor" );
    $( this ).dequeue();
  });
  $( "div" ).slideUp();
});
$( "#stop" ).click(function() {
  $( "div" ).queue( "fx", [] );
  $( "div" ).stop();
});

