example.code:
var input = $( "form input:radio" )
  .wrap( "<span></span>" )
  .parent()
    .css({
      background: "yellow",
      border: "3px red solid"
    });

$("div").text( "For this type jQuery found " + input.length + "." )
  .css("color", "red");
$( "form" ).submit(function() {
  return false;
}); // So it won't submit
