example.css:
div {
  background: #def3ca;
  margin: 3px;
  width: 80px;
  display: none;
  float: left;
  text-align: center;
}

example.code:
function doIt() {
  $( "span,div" ).show( "slow" );
}
// Can pass in function name
$( "button" ).click( doIt );

$( "form" ).submit(function() {
  if ( $( "input" ).val() == "yes" ) {
    $( "p" ).show( 4000, function() {
      $( this ).text( "Ok, DONE! (now showing)" );
    });
  }
  $( "span,div" ).hide( "fast" );
  // To stop the submit
  return false;
});

example.css:
span {
  display: none;
}
div {
  display: none;
}
p {
  font-weight: bold;
  background-color: #fcd;
}
