example.css:
div {
  background: #de9a44;
  margin: 3px;
  width: 80px;
  height: 40px;
  display: none;
  float: left;
}

example.code:
$( "div" ).click(function () {
  $( this ).css({
    borderStyle: "inset",
    cursor: "wait"
  });
$( "input" ).slideDown( 1000,function() {
  $( this ).css( "border", "2px red inset" )
    .filter( ".middle" )
       .css( "background", "yellow" )
       .focus();
  $( "div" ).css( "visibility", "hidden" );
  });
});

