example.code:
var input = $( "form input:text" ).css({
  background: "yellow",
  border: "3px red solid"
});

$( "div" )
  .text( "For this type jQuery found " + input.length + "." )
  .css( "color", "red" );

// Prevent form submission
$( "form" ).submit(function( event ) {
  event.preventDefault();
});
