example.code:
function showWidth( ele, w ) {
  $( "div" ).text( "The width for the " + ele + " is " + w + "px.");
}
$( "#getp" ).click(function() {
  showWidth( "paragraph", $( "p" ).width() );
});
$( "#getd" ).click(function() {
  showWidth("document", $(document).width());
});
$("#getw").click(function() {
  showWidth( "window", $( window ).width() );
});

example.css:
body {
  background: yellow;
}
button {
  font-size: 12px;
  margin: 2px;
}
p {
  width: 150px;
  border: 1px red solid;
}
div {
  color: red;
  font-weight: bold;
}

example.code:
(function() {
  var modWidth = 50;
  $( "div" ).one( "click", function() {
    $( this ).width( modWidth ).addClass( "mod" );
    modWidth -= 8;
  });
})();

example.css:
div {
  width: 70px;
  height: 50px;
  float: left;
  margin: 5px;
  background: red;
  cursor: pointer;
}
.mod {
  background: blue;
  cursor: default;
}
